mapper.select:
select pkid
, code_password as codePassword
,status
,activation_type as activationType
,created
,modified
from tb_serial_code


        pkid = #{pkid}


        and status = #{status}


        and activation_type = #{activationType}


order by pkid
limit ${startRow},${pageSize}

select count(0) from tb_serial_code


        pkid = #{pkid}


        and status = #{status}


        and activation_type = #{activationType}
